    ---------------------------------------------------------------------------
    STS_SKID_BUF_I : entity axi_dma_v7_1_21.axi_dma_skid_buf
        generic map(
            C_WDATA_WIDTH       => C_S_AXIS_S2MM_STS_TDATA_WIDTH
        )
        port map(
